import{d as e}from"./app.a704330d.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const i={},d=e('<h1 id="\u8EAB\u4EFD\u9A8C\u8BC1\u53CA\u8BBF\u95EE\u63A7\u5236" tabindex="-1"><a class="header-anchor" href="#\u8EAB\u4EFD\u9A8C\u8BC1\u53CA\u8BBF\u95EE\u63A7\u5236" aria-hidden="true">#</a> \u8EAB\u4EFD\u9A8C\u8BC1\u53CA\u8BBF\u95EE\u63A7\u5236</h1><h2 id="\u4E00\u3001\u6280\u672F\u6808" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u6280\u672F\u6808" aria-hidden="true">#</a> \u4E00\u3001\u6280\u672F\u6808</h2><ul><li>Spring Security</li><li>JWT</li><li>Redis</li></ul><h2 id="\u4E8C\u3001\u57FA\u672C\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u57FA\u672C\u6982\u5FF5" aria-hidden="true">#</a> \u4E8C\u3001\u57FA\u672C\u6982\u5FF5</h2><ul><li>\u7528\u6237\uFF0C\u5BF9\u5E94\u4F7F\u7528\u8005\uFF0C\u6BCF\u4E2A\u7528\u6237\u53EF\u4EE5\u62E5\u6709\u591A\u4E2A\u89D2\u8272</li><li>\u89D2\u8272\uFF0C\u6BCF\u4E2A\u89D2\u8272\u53EF\u4EE5\u62E5\u6709\u591A\u4E2A\u6743\u9650</li><li>\u6743\u9650\uFF0C\u5F53\u524D\u7248\u672C\u4E3A\u9875\u9762\u7EA7\u6743\u9650\uFF0C\u4E00\u4E2A\u6743\u9650\u5BF9\u5E94\u524D\u7AEF\u7684\u4E00\u4E2A\u9875\u9762\u548C\u540E\u7AEF\u7684\u4E00\u4E2A Controller</li></ul><h2 id="\u4E09\u3001spring-security" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001spring-security" aria-hidden="true">#</a> \u4E09\u3001Spring Security</h2><p>\u5F85\u66F4\u65B0</p><h2 id="\u56DB\u3001jwt" tabindex="-1"><a class="header-anchor" href="#\u56DB\u3001jwt" aria-hidden="true">#</a> \u56DB\u3001JWT</h2><h3 id="_1-\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#_1-\u4F5C\u7528" aria-hidden="true">#</a> 1. \u4F5C\u7528</h3><p>JWT \u6709\u7740\u81EA\u9274\u6743\u7684\u7279\u70B9\uFF0C\u56E0\u6B64\u53EF\u4EE5\u5728\u4E0D\u8017\u8D39\u670D\u52A1\u5668\u5185\u5B58\u7A7A\u95F4\uFF08\u5B58\u50A8 Session\uFF09\u7684\u60C5\u51B5\u4E0B\u5B9E\u73B0\u8BA4\u8BC1\u3002</p><h3 id="_2-\u5B58\u50A8\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#_2-\u5B58\u50A8\u5185\u5BB9" aria-hidden="true">#</a> 2. \u5B58\u50A8\u5185\u5BB9</h3><p>JWT \u4E2D\u5B58\u50A8 <code>ID\u3001username\u3001\u89D2\u8272ID\u5217\u8868\u3001\u7B7E\u53D1\u65F6\u95F4\u3001\u8FC7\u671F\u65F6\u95F4</code> \u3002</p><h3 id="_3-\u4E3A\u4EC0\u4E48\u4E0D\u5B58\u50A8\u6743\u9650" tabindex="-1"><a class="header-anchor" href="#_3-\u4E3A\u4EC0\u4E48\u4E0D\u5B58\u50A8\u6743\u9650" aria-hidden="true">#</a> 3. \u4E3A\u4EC0\u4E48\u4E0D\u5B58\u50A8\u6743\u9650\uFF1F</h3><ul><li>\u5982\u679C\u5B58\u50A8\uFF0CJWT \u4F53\u79EF\u52BF\u5FC5\u4F1A\u589E\u5927\uFF0C\u8BF7\u6C42\u4E2D\u643A\u5E26\u5B83\u5C06\u6781\u5927\u5730\u8017\u8D39\u7F51\u7EDC\u8D44\u6E90</li><li>\u5982\u679C\u5B58\u50A8\uFF0C\u5F53\u89D2\u8272\u6240\u5C5E\u7684\u6743\u9650\u53D1\u751F\u53D8\u66F4\u65F6\u5C06\u96BE\u4EE5\u5904\u7406</li><li>\u6BCF\u4E2A\u89D2\u8272\u62E5\u6709\u7684\u6743\u9650\u5728\u540C\u4E00\u65F6\u95F4\u662F\u76F8\u540C\u7684\uFF0C\u53EF\u4EE5\u65B9\u4FBF\u5730\u7F13\u5B58\u3001&quot;\u8F7B\u677E&quot; \u5730\u83B7\u53D6</li></ul><h2 id="\u4E94\u3001redis" tabindex="-1"><a class="header-anchor" href="#\u4E94\u3001redis" aria-hidden="true">#</a> \u4E94\u3001Redis</h2><h3 id="_1-\u7F13\u5B58\u88AB\u7981\u7528\u7528\u6237" tabindex="-1"><a class="header-anchor" href="#_1-\u7F13\u5B58\u88AB\u7981\u7528\u7528\u6237" aria-hidden="true">#</a> 1. \u7F13\u5B58\u88AB\u7981\u7528\u7528\u6237</h3><h4 id="_1-\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#_1-\u80CC\u666F" aria-hidden="true">#</a> (1) \u80CC\u666F</h4><p>\u5982\u679C\u7528\u6237\u88AB\u7981\u7528\uFF0C\u8BE5\u7528\u6237\u6570\u636E\u5E93\u4E2D\u7684 status \u5B57\u6BB5\u5C06\u4F1A\u88AB\u8BBE\u4E3A true\uFF0C\u4ED6\u5C06\u4E0D\u518D\u80FD\u8FDB\u884C\u4EFB\u4F55\u64CD\u4F5C\u3002</p><p>\u7528\u6237\u7684\u7981\u7528\u9700\u8981\u4E24\u90E8\u5206\u5DE5\u4F5C\uFF1A</p><ul><li>\u4E0D\u5F97\u767B\u5F55\uFF1A\u7531\u4E8E\u767B\u5F55\u65F6\u672C\u6765\u5C31\u8981\u67E5\u8BE2\u6570\u636E\u5E93\uFF0C\u56E0\u6B64\u53EA\u9700\u8981\u7B80\u5355\u5224\u65AD\u4E00\u4E0B\u7528\u6237\u5B9E\u4F53\u7684 status \u5C5E\u6027\u5373\u53EF</li><li>\u4E0D\u5F97\u53D1\u9001\u8BF7\u6C42\uFF1A\u7531\u4E8E JWT \u662F\u81EA\u9274\u6743\u7684\uFF0C\u4E3A\u4E86\u80FD\u591F\u7981\u7528\u7528\u6237\uFF0C\u6211\u4EEC\u5FC5\u987B\u989D\u5916\u8FDB\u884C\u67E5\u8BE2\u6570\u636E\u5E93\u7684\u64CD\u4F5C\uFF0C\u4E3A\u4E86\u51CF\u8F7B\u6570\u636E\u5E93\u8D1F\u62C5\u3001\u63D0\u5347\u67E5\u8BE2\u6548\u7387\uFF0C\u53EF\u4EE5\u5229\u7528 Redis \u7F13\u5B58\u6570\u636E\u5E93\u4E2D\u6240\u6709\u88AB\u7981\u7528\u7528\u6237</li></ul><h4 id="_2-\u601D\u8DEF" tabindex="-1"><a class="header-anchor" href="#_2-\u601D\u8DEF" aria-hidden="true">#</a> (2) \u601D\u8DEF</h4><p>\u5C06\u5F53\u524D\u6240\u6709\u7684 &quot;\u88AB\u7981\u7528\u7528\u6237&quot; \u7F13\u5B58\uFF0C\u5F53 <code>JWTVerifyFilter</code> \u63A5\u6536\u5230\u8BF7\u6C42\u65F6\uFF0C\u67E5\u8BE2\u7F13\u5B58\uFF0C\u5224\u65AD\u5F53\u524D\u7528\u6237\u662F\u5426\u88AB\u7981\u7528\u3002</p><h4 id="_3-\u5177\u4F53\u5B9E\u73B0" tabindex="-1"><a class="header-anchor" href="#_3-\u5177\u4F53\u5B9E\u73B0" aria-hidden="true">#</a> (3) \u5177\u4F53\u5B9E\u73B0</h4><ul><li>\u5728 <code>UmsAdminService</code> \u4E2D\u589E\u52A0 <code>listDisabled()</code> \u65B9\u6CD5\uFF0C\u5B83\u5C06\u4F1A\u5411\u6570\u636E\u5E93\u4E2D\u67E5\u8BE2\u6240\u6709\u88AB\u7981\u7528\u7684\u7528\u6237</li><li>\u4E3A <code>listDisabled()</code> \u65B9\u6CD5\u589E\u52A0 <code>@Cacheable(value = &quot;DisabledAdmin&quot;)</code>\uFF0C\u4F7F SpringBoot \u81EA\u52A8\u7F13\u5B58\u5176\u7ED3\u679C</li><li>\u4E3A <strong>\u589E\u3001\u5220</strong> \u65B9\u6CD5\u6DFB\u52A0 <code>@CacheEvict</code> \u6CE8\u89E3\uFF0C\u5F53\u6539\u52A8\u6D89\u53CA status \u5B57\u6BB5\u65F6\u6E05\u7A7A\u7F13\u5B58</li><li>\u5728\u767B\u5F55\u72B6\u6001\u8BFB\u53D6\u5668 <code>JWTVerifyFilter</code> \u4E2D\u8C03\u7528 <code>listDisabled()</code> \u65B9\u6CD5\u83B7\u53D6\u6240\u6709\u88AB\u7981\u7528\u7528\u6237\uFF0C\u5224\u65AD\u5F53\u524D\u7528\u6237\u662F\u5426\u88AB\u7981\u7528</li></ul><h3 id="_2-\u4F7F\u5DF2\u7B7E\u53D1\u7684-jwt-\u5931\u6548" tabindex="-1"><a class="header-anchor" href="#_2-\u4F7F\u5DF2\u7B7E\u53D1\u7684-jwt-\u5931\u6548" aria-hidden="true">#</a> 2. \u4F7F\u5DF2\u7B7E\u53D1\u7684 JWT \u5931\u6548</h3><h4 id="_1-\u80CC\u666F-1" tabindex="-1"><a class="header-anchor" href="#_1-\u80CC\u666F-1" aria-hidden="true">#</a> (1) \u80CC\u666F</h4><p>JWT \u62E5\u6709\u81EA\u6821\u9A8C\u7684\u7279\u6027\uFF0C\u8FD9\u4F7F\u5F97\u5B83\u53EF\u4EE5\u81EA\u884C\u4FDD\u5B58\u4FE1\u606F\uFF0C\u4ECE\u800C\u66FF\u4EE3 Session\u3002</p><p>\u4F46 JWT \u7684\u81EA\u6821\u9A8C\u673A\u5236\u4E5F\u5E26\u6765\u4E86\u4E00\u4E2A\u95EE\u9898\uFF0CJWT \u4E00\u65E6\u7B7E\u53D1\uFF0C\u5728\u5176\u6709\u6548\u671F\u5185\u5B83\u5C06\u59CB\u7EC8\u6709\u6548\uFF0C\u8FD9\u5BFC\u81F4\u7528\u6237\u7684\u8BA4\u8BC1\u662F\u4E0D\u53EF\u63A7\u7684\uFF0C\u6211\u4EEC\u65E0\u6CD5\u5B9E\u73B0\u8BF8\u5982\u5F3A\u5236\u4E0B\u7EBF\u67D0\u7528\u6237\u7684\u64CD\u4F5C\u3002</p><h4 id="_2-\u601D\u8DEF-1" tabindex="-1"><a class="header-anchor" href="#_2-\u601D\u8DEF-1" aria-hidden="true">#</a> (2) \u601D\u8DEF</h4><p>\u901A\u8FC7 &quot;\u5916\u6302\u6821\u9A8C&quot; \u7684\u65B9\u5F0F\u89E3\u51B3\u3002</p><p>\u7F13\u5B58\u7528\u6237 ID \u53CA\u6700\u65E9\u6709\u6548\u65F6\u95F4\uFF08\u8BBE\u4E3A\u5F53\u524D\u65F6\u95F4\uFF09\uFF0C\u63A5\u6536\u5230 JWT \u65F6\uFF0C\u5224\u65AD\u5176\u662F\u5426\u6709\u5BF9\u5E94\u7684\u7F13\u5B58\uFF0C\u5982\u679C\u6709\uFF0C\u5224\u65AD\u5176\u662F\u5426\u65E9\u4E8E\u6700\u65E9\u6709\u6548\u65F6\u95F4\u9881\u53D1\uFF0C\u5982\u679C\u662F\uFF0C\u5219\u8981\u6C42\u91CD\u65B0\u767B\u5F55\u3002</p><p>\u5982\u6B64\uFF0C\u6211\u4EEC\u4FBF\u5B9E\u73B0\u4E86\u5C06\u5DF2\u7B7E\u53D1\u7684 JWT \u5F3A\u5236\u5931\u6548\uFF0C\u8981\u6C42\u7528\u6237\u91CD\u65B0\u767B\u5F55\u7684\u613F\u671B\u3002</p><h4 id="_3-\u5177\u4F53\u5B9E\u73B0-1" tabindex="-1"><a class="header-anchor" href="#_3-\u5177\u4F53\u5B9E\u73B0-1" aria-hidden="true">#</a> (3) \u5177\u4F53\u5B9E\u73B0</h4><ul><li>\u5728 <code>UmsAdminService</code> \u4E2D\u589E\u52A0 <code>invalidateIssuedJWT(Long id)</code> \u65B9\u6CD5\uFF0C\u5B83\u63A5\u6536\u7528\u6237 ID\uFF0C\u5E76\u5C06\u7528\u6237 ID \u53CA\u5F53\u524D\u65F6\u95F4\u5B58\u50A8\u8FDB\u7F13\u5B58\u4E4B\u4E2D</li><li>\u5728 <code>UmsAdminService</code> \u4E2D\u589E\u52A0 <code>isJWTInvalidated(Long id, Long time)</code> \u65B9\u6CD5\uFF0C\u5B83\u63A5\u6536\u7528\u6237 ID \u53CA JWT \u7684\u7B7E\u53D1\u65F6\u95F4\uFF0C\u8FD4\u56DE JWT \u662F\u5426\u5931\u6548</li><li>\u5728\u6211\u4EEC\u5E0C\u671B JWT \u5931\u6548\u7684\u5178\u578B\u573A\u666F\uFF0C\u4F8B\u5982\u5220\u9664\u7528\u6237\u3001\u4FEE\u6539\u5BC6\u7801\uFF0C\u8C03\u7528 <code>invalidateIssuedJWT(Long id)</code> \u65B9\u6CD5\u4F7F\u5DF2\u7B7E\u53D1\u7684 JWT \u5931\u6548</li><li>\u5728\u767B\u5F55\u72B6\u6001\u8BFB\u53D6\u5668 <code>JWTVerifyFilter</code> \u4E2D\uFF0C\u9996\u5148\u89E3\u6790\u83B7\u5F97\u7528\u6237 ID \u53CA\u7B7E\u53D1\u65F6\u95F4\uFF0C\u7136\u540E\u8C03\u7528 <code>isJWTInvalidated(Long id, Long time)</code> \u5224\u65AD JWT \u662F\u5426\u5931\u6548</li></ul><h3 id="_3-\u7F13\u5B58\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650" tabindex="-1"><a class="header-anchor" href="#_3-\u7F13\u5B58\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650" aria-hidden="true">#</a> 3. \u7F13\u5B58\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650</h3><h4 id="_1-\u80CC\u666F-2" tabindex="-1"><a class="header-anchor" href="#_1-\u80CC\u666F-2" aria-hidden="true">#</a> (1) \u80CC\u666F</h4><p><code>JWTVerifyFilter</code> \u8D1F\u8D23\u89E3\u6790\u8BF7\u6C42\u4E2D\u7684 JWT\u3001\u8BFB\u53D6\u767B\u5F55\u72B6\u6001\u3002</p><p>\u5F53 <code>JWTVerifyFilter</code> \u63A5\u6536\u5230\u8BF7\u6C42\u65F6\uFF0C\u9700\u8981\u6839\u636E JWT \u4E2D\u5B58\u50A8\u7684\u89D2\u8272\u4FE1\u606F\u83B7\u53D6\u5F53\u524D\u7528\u6237\u6240\u62E5\u6709\u7684\u6743\u9650\uFF0C\u5E76\u5C06\u6743\u9650\u4FE1\u606F\u63D0\u4F9B\u7ED9 Spring Security \u6846\u67B6\u3002</p><h4 id="_2-\u601D\u8DEF-2" tabindex="-1"><a class="header-anchor" href="#_2-\u601D\u8DEF-2" aria-hidden="true">#</a> (2) \u601D\u8DEF</h4><p>\u5C06\u6BCF\u4E2A\u89D2\u8272\uFF08\u901A\u8FC7 ID \u6807\u8BC6\uFF09\u5BF9\u5E94\u7684\u6743\u9650\u4FE1\u606F\u7F13\u5B58\uFF0C\u5F53\u63A5\u6536\u5230\u8BF7\u6C42\u65F6\uFF0C\u904D\u5386\u89D2\u8272\u4FE1\u606F\uFF0C\u83B7\u53D6\u6BCF\u4E2A\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\uFF0C\u5BF9\u6240\u6709\u7684\u6743\u9650\u4FE1\u606F\u8FDB\u884C\u6574\u5408\u3001\u53BB\u91CD\u5904\u7406\uFF0C\u653E\u5165 Spring Security \u5B89\u5168\u4E0A\u4E0B\u6587\u4E4B\u4E2D\u3002</p><h3 id="_4-\u7F13\u5B58\u83DC\u5355" tabindex="-1"><a class="header-anchor" href="#_4-\u7F13\u5B58\u83DC\u5355" aria-hidden="true">#</a> 4. \u7F13\u5B58\u83DC\u5355</h3><h4 id="_1-\u80CC\u666F-3" tabindex="-1"><a class="header-anchor" href="#_1-\u80CC\u666F-3" aria-hidden="true">#</a> (1) \u80CC\u666F</h4><p>\u5728\u672C\u7CFB\u7EDF\u4E2D\uFF0C\u7528\u6237\u767B\u5F55\u540E\u9700\u8981\u8BF7\u6C42 <code>/account/router</code> \u63A5\u53E3\u4EE5\u83B7\u53D6\u5F53\u524D\u7528\u6237\u7684\u8DEF\u7531\u4FE1\u606F\uFF0C\u4ECE\u800C\u6784\u5EFA\u524D\u7AEF\u7CFB\u7EDF\u3002</p><p>\u8BE5\u63A5\u53E3\u7684\u8BBF\u95EE\u662F\u8F83\u4E3A\u9AD8\u9891\u7684\uFF0C\u4E14\u4E00\u6B21\u8BBF\u95EE\u53EF\u80FD\u4F1A\u591A\u6B21\u67E5\u8BE2\u6570\u636E\u5E93\uFF0C\u56E0\u6B64\u7F13\u5B58\u662F\u5341\u5206\u6709\u5FC5\u8981\u7684\u3002</p><h4 id="_2-\u601D\u8DEF-3" tabindex="-1"><a class="header-anchor" href="#_2-\u601D\u8DEF-3" aria-hidden="true">#</a> (2) \u601D\u8DEF</h4><p>\u9996\u5148\uFF0C\u540E\u7AEF\u4F1A\u6839\u636E Spring Security \u5B89\u5168\u4E0A\u4E0B\u6587\u4E2D\u7684\u6743\u9650\u4FE1\u606F\u83B7\u53D6\u7528\u6237\u6240\u80FD\u8BBF\u95EE\u7684\u9875\u9762\uFF0C\u56E0\u6B64\u5E94\u8BE5\u7F13\u5B58\u7531\u7F16\u7801\u67E5\u8BE2\u7684\u83DC\u5355\uFF1B</p><p>\u5176\u6B21\uFF0C\u4E3A\u4E86\u7EC4\u5408\u4E3A\u6811\u5F62\uFF0C\u4F1A\u9012\u5F52\u5730\u6839\u636E parentId \u67E5\u8BE2\u7236\u7EA7\u83DC\u5355\uFF0C\u56E0\u6B64\u5E94\u8BE5\u7F13\u5B58\u7531 ID \u67E5\u8BE2\u7684\u83DC\u5355\u3002</p><h2 id="\u516D\u3001\u51E0\u4E2A\u5C0F\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u516D\u3001\u51E0\u4E2A\u5C0F\u95EE\u9898" aria-hidden="true">#</a> \u516D\u3001\u51E0\u4E2A\u5C0F\u95EE\u9898</h2><h3 id="_1-\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#_1-\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" aria-hidden="true">#</a> 1. \u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E\uFF1F</h3><p>JWT \u4E2D\u5E76\u4E0D\u643A\u5E26\u6743\u9650\u4FE1\u606F\uFF0C\u800C\u662F\u901A\u8FC7\u89D2\u8272\u4FE1\u606F\u5411\u7F13\u5B58\u83B7\u53D6\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u9002\u65F6\u6E05\u7A7A\u7F13\u5B58\u91CD\u65B0\u83B7\u53D6\uFF0C\u4FBF\u53EF\u4EE5\u4FDD\u8BC1\u89D2\u8272\u5BF9\u5E94\u7684\u6743\u9650\u4FE1\u606F\u771F\u5B9E\u6709\u6548\u3002</p><h3 id="_2-\u7528\u6237\u8DEF\u7531\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#_2-\u7528\u6237\u8DEF\u7531\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" aria-hidden="true">#</a> 2. \u7528\u6237\u8DEF\u7531\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E\uFF1F</h3><p>\u9996\u5148\u7406\u6E05\u4E00\u4E2A\u6982\u5FF5\uFF0C\u672C\u7CFB\u7EDF\u4E2D\u8DEF\u7531\u540C\u65F6\u6307\u4EE3\u7CFB\u7EDF\u5BFC\u822A\u680F\u53CA Vue Router \u4E2D\u7684\u8DEF\u7531\uFF0C\u5B83\u4EEC\u5747\u7531\u76F8\u540C\u7684\u8DEF\u7531\u4FE1\u606F\u751F\u6210\u3002</p><p>\u672C\u7CFB\u7EDF\u91C7\u53D6\u4E86\u52A8\u6001\u8DEF\u7531\u7684\u65B9\u5F0F\uFF0C\u7528\u6237\u767B\u5F55\u6210\u529F\u540E\u5E94\u9996\u5148\u5411 &quot;\u8DEF\u7531\u63A5\u53E3&quot; \u53D1\u9001\u8BF7\u6C42\uFF0C\u540E\u7AEF\u5C06\u4F1A\u901A\u8FC7 <code>\u7528\u6237 - \u89D2\u8272 - \u6743\u9650 - \u8DEF\u7531</code> \u7684\u65B9\u5F0F\u751F\u6210\u8DEF\u7531\u4FE1\u606F\u5E76\u8FD4\u56DE\u3002</p><p>\u540E\u7AEF\u53EF\u4EE5\u4FDD\u8BC1\u7684\u662F\u6BCF\u6B21\u8BF7\u6C42 &quot;\u8DEF\u7531\u63A5\u53E3&quot; \u65F6\u90FD\u53EF\u4EE5\u83B7\u53D6\u5230\u6700\u65B0\u7684\u8DEF\u7531\u4FE1\u606F\uFF0C\u4F46\u7ED3\u5408\u524D\u7AEF\u4FBF\u4F1A\u51FA\u73B0\u4E00\u4E9B\u95EE\u9898\uFF1A</p><ul><li>\u5982\u679C\u4E0D\u5BF9\u8DEF\u7531\u4FE1\u606F\u8FDB\u884C\u6301\u4E45\u5316\uFF0C\u53EF\u4EE5\u5C3D\u53EF\u80FD\u5730\u4FDD\u8BC1\u8DEF\u7531\u4FE1\u606F\u7684\u53CA\u65F6\u6027\uFF0C\u4F46\u6BCF\u6B21\u5237\u65B0\u9875\u9762\u90FD\u9700\u8981\u91CD\u65B0\u83B7\u53D6\u8DEF\u7531\uFF0C\u8FD9\u5C06\u6781\u5927\u5730\u8017\u8D39\u7F51\u7EDC\u8D44\u6E90</li><li>\u5982\u679C\u5BF9\u8DEF\u7531\u4FE1\u606F\u8FDB\u884C\u6301\u4E45\u5316\uFF0C\u8DEF\u7531\u4FE1\u606F\u5C06\u4F1A\u88AB\u5B58\u50A8\u5728\u6D4F\u89C8\u5668\u4F1A\u8BDD\u5B58\u50A8\u7A7A\u95F4\u4E2D\uFF0C\u5237\u65B0\u9875\u9762\u65E0\u9700\u91CD\u65B0\u83B7\u53D6\uFF0C\u4F46\u53EF\u80FD\u4F1A\u5BFC\u81F4\u8DEF\u7531\u4FE1\u606F\u4E0D\u53CA\u65F6</li></ul><p>\u7EFC\u5408\u8003\u8651\u4E0B\uFF0C\u672C\u7CFB\u7EDF\u9009\u7528\u4E86\u5BF9\u8DEF\u7531\u4FE1\u606F\u6301\u4E45\u5316\u7684\u65B9\u6CD5\uFF0C\u539F\u56E0\u5982\u4E0B\uFF1A</p><ul><li>\u89D2\u8272\u5BF9\u5E94\u6743\u9650\u7684\u6539\u52A8\u4E0D\u662F\u5F88\u9891\u7E41\uFF0C\u6CA1\u6709\u5FC5\u8981\u4E3A\u6B64\u5E73\u767D\u8017\u8D39\u7F51\u7EDC\u8D44\u6E90</li><li>\u5982\u679C\u7528\u6237\u7684\u6743\u9650\u8FDB\u884C\u6269\u5145\uFF0C\u8DEF\u7531\u672A\u53D1\u751F\u53CA\u65F6\u66F4\u65B0\uFF0C\u63A8\u8350\u7684\u89E3\u51B3\u65B9\u6848\u662F\u8BA9\u7528\u6237\u91CD\u65B0\u767B\u5F55</li><li>\u5982\u679C\u7528\u6237\u7684\u6743\u9650\u8FDB\u884C\u7F29\u7A84\uFF0C\u8DEF\u7531\u672A\u53D1\u751F\u53CA\u65F6\u66F4\u65B0\uFF0C\u5982\u679C\u7528\u6237\u8FDB\u5165\u4E86\u5DF2\u7ECF\u4E0D\u5C5E\u4E8E\u5B83\u7684\u9875\u9762\uFF0C\u540E\u7AEF\u4F1A\u8FD4\u56DE &quot;\u6743\u9650\u9A8C\u8BC1\u9519\u8BEF\u72B6\u6001\u7801&quot;\uFF0C\u524D\u7AEF\u91CD\u65B0\u8FDB\u884C\u8DEF\u7531\u4FE1\u606F\u7684\u83B7\u53D6</li></ul><h3 id="_3-\u7528\u6237\u5BF9\u5E94\u7684\u89D2\u8272\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#_3-\u7528\u6237\u5BF9\u5E94\u7684\u89D2\u8272\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" aria-hidden="true">#</a> 3. \u7528\u6237\u5BF9\u5E94\u7684\u89D2\u8272\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E\uFF1F</h3><p>\u4F7F\u7528\u6237\u5BF9\u5E94\u7684\u5DF2\u7B7E\u53D1\u7684 JWT \u5931\u6548\uFF0C\u8981\u6C42\u91CD\u65B0\u767B\u9646\u3002</p><h3 id="_4-\u7528\u6237\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#_4-\u7528\u6237\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E" aria-hidden="true">#</a> 4. \u7528\u6237\u53D1\u751F\u66F4\u65B0\u600E\u4E48\u529E\uFF1F</h3><p>\u7528\u6237\u4FE1\u606F\u7684\u4FEE\u6539\u5206\u4E3A\u4EE5\u4E0B\u51E0\u79CD\u60C5\u51B5\uFF1A</p><h4 id="_1-\u57FA\u672C\u4FE1\u606F\u4FEE\u6539" tabindex="-1"><a class="header-anchor" href="#_1-\u57FA\u672C\u4FE1\u606F\u4FEE\u6539" aria-hidden="true">#</a> (1) \u57FA\u672C\u4FE1\u606F\u4FEE\u6539</h4><p>\u5728\u672C\u7CFB\u7EDF\u4E2D\uFF0CJWT \u4F5C\u4E3A\u8BA4\u8BC1\u51ED\u636E\u800C\u975E\u4FE1\u606F\u6765\u6E90\uFF0C\u56E0\u6B64\u7528\u6237\u4FE1\u606F\u7684\u66F4\u6539\uFF08\u5305\u62EC\u7528\u6237\u540D\uFF09\u65E0\u9700\u53CD\u6620\u5230 JWT \u4E4B\u4E0A\u3002</p><h4 id="_2-\u5BC6\u7801\u4FEE\u6539" tabindex="-1"><a class="header-anchor" href="#_2-\u5BC6\u7801\u4FEE\u6539" aria-hidden="true">#</a> (2) \u5BC6\u7801\u4FEE\u6539</h4><p>\u5982\u679C\u4FEE\u6539\u4E86 password \u5B57\u6BB5\uFF0C\u4FBF\u662F\u8FDB\u884C\u4E86\u5BC6\u7801\u4FEE\u6539\uFF0C\u6B64\u65F6\u5E94\u4F7F\u7528\u6237\u5BF9\u5E94\u7684\u5DF2\u7B7E\u53D1\u7684 JWT \u5931\u6548\uFF0C\u8981\u6C42\u91CD\u65B0\u767B\u9646\u3002</p><h4 id="_3-\u7528\u6237\u7981\u7528" tabindex="-1"><a class="header-anchor" href="#_3-\u7528\u6237\u7981\u7528" aria-hidden="true">#</a> (3) \u7528\u6237\u7981\u7528</h4><p>\u5982\u679C\u4FEE\u6539\u4E86 status \u5B57\u6BB5\uFF0C\u5C06\u7528\u6237\u7981\u7528\uFF0C\u6B64\u65F6\u5E94\u8BE5\u66F4\u65B0\u7F13\u5B58\u4E2D\u7684\u7981\u7528\u7528\u6237\u540D\u5355\u3002</p>',67);function r(h,t){return d}var l=a(i,[["render",r]]);export{l as default};
